def addStaticUDA( name="dummy", object="net", type="float"): # define visum type numbers of data types dict_types = {"str": 5, "float": 2, "int": 1, "bool": 9}
# type to type number typeno = dict_types[type.lower()]
object = object[0].upper() + object[1:]
# design of function call of ... .AddUserDefinedAttribute(id, short name, long name, value type) if object.lower() == "net": # case: net object str_method_call = "Visum.Net" + ".AddUserDefinedAttribute('" \ + name + "','" \ + name + "','" \ + name + "'," \ + str(typeno) + ")" else: # all other objects str_method_call = "Visum.Net." + object + ".AddUserDefinedAttribute('"\ + name + "','" \ + name + "','" \ + name + "'," \ + str(typeno) + ")"
try: eval(str_method_call) print("UDA", name, object, " is created successfully") except: print("UDA", name, object, "is already defined")
def open_visum(name): try: ''' VisumStart - enables the usage in the procedure sequence or as Visum instance - name of the Visum version that is opened ''' global Visum Visum name = Visum.UserPreferences.DocumentName except NameError: import win32com.client as com print('initialize visum instance') Visum = com.Dispatch("Visum.Visum.200") print('open visum file: {}'.format(name)) Visum.LoadVersion(name) print('erfolgreich geladen')
return Visum # ====== Core ==========
str_version = r"D:\Seafile\35_VRS\Version\35_VRS_Test_AM.ver" Visum = open_visum(str_version)
# insert UDA data here # format: [name, object, data type] # e.g: ["dummy", "links", "float"] list_UDA = [ ["LinTimExport", "TSystems", "bool"], ["TSys_LinTim_Modification", "TSystems", "bool"], ]
for uda in list_UDA: addStaticUDA(uda[0], uda[1], uda[2])

import numpy as np
from numpy import *

def get_tsys():
    tsys = Visum.Net.TSystems.GetMultipleAttributes(["Code", "Type"], True)
    oev = []
    for ii in range(len(tsys)):
        if tsys[ii][1] == 'PUT':
            oev.append(tsys[ii][0])
    return oev

systems = get_tsys()
for ii in range(len(systems)):
    attr_links = ["REVERSELINK\T_PUTSYS(" + systems[ii] + ")", "T_PUTSYS(" + systems[ii] + ")"]
    all_links = array(Visum.Net.Links.GetMultipleAttributes(attr_links),dtype=int)
    for jj in range(len(all_links)):
        if all_links[jj][1] == 0:
            if all_links[jj][0] != 0:
                all_links[jj][1] = all_links[jj][0]
        all_links[jj][0] = jj + 1
    Visum.Net.Links.SetMultiAttValues("T_PUTSYS(" + systems[ii] + ")", all_links)

# Set Filter
LinkFilter = Visum.Filters.LinkFilter()
LinkFilter.Init()
LinkFilter.AddCondition("OP_None", False, "TSysSet", "NotEqualAtt", "ReverseLink\TSysSet")
LinkFilter.UseFilter = True

# Change TSysSet
Attr_Links = ["TSysSet", "ReverseLink\TSysSet"]
try:
	links = list(Visum.Net.Links.GetMultipleAttributes(Attr_Links, OnlyActive=True))
except:
	links = []
if len(links) > 0:
	for ii in range(len(links)):
		links[ii] = list(links[ii])
		tmp_to_link = links[ii][1].split(",")
		for kk in range(len(tmp_to_link)):
			if tmp_to_link[kk] not in links[ii][0]:
				links[ii][0] += "," + tmp_to_link[kk]

	Visum.Net.Links.SetMultipleAttributes(Attr_Links, links, True)

# GetAllTSys-PuT
tsys = Visum.Net.TSystems.GetMultipleAttributes(["Code", "Type"], True)
oev = []
for ii in range(len(tsys)):
    if tsys[ii][1] == 'PUT':
        oev.append(tsys[ii][0])

for jj in range(len(oev)):
    LinkFilter = Visum.Filters.LinkFilter()
    LinkFilter.Init()
    LinkFilter.AddCondition("OP_None", False, "TSysSet", "ContainsOneOf", oev[jj])
    LinkFilter.UseFilter = True
    
    
    Attr_Links = ["T_PuTSys(" + oev[jj] + ")", "LinkType\VDef_PuTSys(" + oev[jj] + ")", "Length"]     # [TPuTSys(...), LinkType\VDef_PuTSys(...), Length]
    links = list(Visum.Net.Links.GetMultipleAttributes(Attr_Links, OnlyActive=True))
    for hh in range(len(links)):
        links[hh] = list(links[hh])

#calculate T_PuTSys if it is 0, and round it. If still 0 --> 60s
    for ll in range(len(links)):
        if links[ll][0] == 0:
            links[ll][0] = round((links[ll][2]/links[ll][1])*60)*60
            if links[ll][0] == 0:
                links[ll][0] = 60

    if links != []:
        Visum.Net.Links.SetMultipleAttributes(Attr_Links, links, True)

import numpy as np
from numpy import *

def get_tsys():
    tsys = Visum.Net.TSystems.GetMultipleAttributes(["Code", "Type"], True)
    oev = []
    for ii in range(len(tsys)):
        if tsys[ii][1] == 'PUT':
            oev.append(tsys[ii][0])
    return oev

systems = get_tsys()
for ii in range(len(systems)):
    attr_links = ["REVERSELINK\T_PUTSYS(" + systems[ii] + ")", "T_PUTSYS(" + systems[ii] + ")"]
    all_links = array(Visum.Net.Links.GetMultipleAttributes(attr_links),dtype=int)
    for jj in range(len(all_links)):
        if all_links[jj][1] > all_links[jj][0]:
            if all_links[jj][0] != 0:
                all_links[jj][1] = all_links[jj][0]
        all_links[jj][0] = jj + 1
    Visum.Net.Links.SetMultiAttValues("T_PUTSYS(" + systems[ii] + ")", all_links)

#Remove TSys
tsys = Visum.Net.TSystems.GetMultipleAttributes(["Type", "Code", "LINTIMEXPORT"], True)
for ii in range(len(tsys)):
	if tsys[ii][0] == 'PUT' and tsys[ii][2] == 0:
